/**
 * Look up an alarm code and return diagnosis + fix recommendations
 * @param {string} controllerId - Controller identifier
 * @param {string} alarmCode - Alarm code entered by user
 * @returns {object} Alarm details with recommended fixes
 */
function lookupAlarmCode(controllerId, alarmCode) {
    // Normalize alarm code (remove spaces, uppercase)
    const normalizedCode = alarmCode.toString().trim().toUpperCase();

    // Map controller ID to various database keys
    const controllerMappings = {
        // Original mappings
        'haas_ngc': ['haas_ngc', 'haas_ngc', 'haas_ngc'],
        'haas_classic': ['haas_classic', 'haas_ngc', 'haas_ngc'],
        'okuma_osp_p300m': ['okuma_osp_p300m', 'okuma_osp_p300m', 'okuma'],
        'okuma_osp_p300l': ['okuma_osp_p300l', 'okuma_osp_p300l', 'okuma'],
        'fanuc_0i': ['fanuc_0i', 'fanuc', 'fanuc'],
        'fanuc_31i': ['fanuc_31i', 'fanuc', 'fanuc'],
        'hurco_winmax': ['hurco_winmax', 'hurco_winmax', 'hurco_winmax'],
        'siemens_840d': ['siemens_840d', 'siemens_840d', 'siemens_840d'],
        'mazak_smooth': ['mazak_smooth', 'mazak_smooth', 'mazak'],
        'dmgmori_celos': ['dmgmori_celos', 'dmgmori_celos', 'dmgmori_celos'],
        'makino_pro': ['makino_pro', 'makino_pro', 'makino_pro'],
        'heidenhain_tnc640': ['heidenhain_tnc640', 'heidenhain_tnc640', 'heidenhain_tnc640'],
        'brother_cnc': ['brother_cnc', 'brother_cnc', 'brother_cnc'],
        'doosan_fanuc': ['doosan_fanuc', 'doosan_fanuc', 'doosan'],
        // New mappings for COMPLETE_ALARM_DATABASE
        'mitsubishi': ['mitsubishi', 'mitsubishi', 'mitsubishi'],
        'mitsubishi_m800': ['mitsubishi', 'mitsubishi', 'mitsubishi'],
        'mitsubishi_m700': ['mitsubishi', 'mitsubishi', 'mitsubishi'],
        'yasnac': ['yasnac', 'yasnac', 'yasnac'],
        'yaskawa': ['yasnac', 'yasnac', 'yasnac'],
        'citizen': ['citizen', 'citizen', 'citizen'],
        'citizen_cincom': ['citizen', 'citizen', 'citizen'],
        'star': ['star', 'star', 'star'],
        'star_swiss': ['star', 'star', 'star'],
        'tsugami': ['tsugami', 'tsugami', 'tsugami'],
        'nakamura_tome': ['nakamura_tome', 'nakamura_tome', 'nakamura_tome'],
        'hardinge': ['hardinge', 'hardinge', 'hardinge'],
        'index': ['index_traub', 'index_traub', 'index_traub'],
        'traub': ['index_traub', 'index_traub', 'index_traub'],
        'matsuura': ['matsuura', 'matsuura', 'matsuura'],
        'kitamura': ['kitamura', 'kitamura', 'kitamura'],
        'mori_seiki': ['mori_seiki', 'mori_seiki', 'mori_seiki'],
        'sodick': ['sodick', 'sodick', 'sodick'],
        'fadal': ['fadal', 'fadal', 'fadal'],
        'hermle': ['hermle', 'hermle', 'hermle'],
        'chiron': ['chiron', 'chiron', 'chiron'],
        'grob': ['grob', 'grob', 'grob'],
        'toyoda': ['toyoda', 'toyoda', 'toyoda'],
        'okk': ['okk', 'okk', 'okk'],
        'hwacheon': ['hwacheon', 'hwacheon', 'hwacheon'],
        'hyundai_wia': ['hyundai_wia', 'hyundai_wia', 'hyundai_wia'],
        'doosan': ['doosan', 'doosan', 'doosan'],
        'dn_solutions': ['doosan', 'doosan', 'doosan'],
        'ycm': ['ycm', 'ycm', 'ycm'],
        'feeler': ['feeler', 'feeler', 'feeler'],
        'leadwell': ['leadwell', 'leadwell', 'leadwell'],
        'chevalier': ['chevalier', 'chevalier', 'chevalier']
    };
    const mappings = controllerMappings[controllerId] || [controllerId, controllerId, controllerId];

    // Search order: COMPLETE_ALARM_DATABASE, EXPANDED_ALARM_DATABASE, COMPREHENSIVE_ALARM_DATABASE, MACHINE_ALARM_DATABASE
    const databases = [
        { name: 'complete', db: typeof COMPLETE_ALARM_DATABASE !== 'undefined' ? COMPLETE_ALARM_DATABASE : null, key: mappings[2] },
        { name: 'expanded', db: typeof EXPANDED_ALARM_DATABASE !== 'undefined' ? EXPANDED_ALARM_DATABASE : null, key: mappings[2] },
        { name: 'comprehensive', db: typeof COMPREHENSIVE_ALARM_DATABASE !== 'undefined' ? COMPREHENSIVE_ALARM_DATABASE : null, key: mappings[1] },
        { name: 'basic', db: typeof MACHINE_ALARM_DATABASE !== 'undefined' ? MACHINE_ALARM_DATABASE : null, key: mappings[0] }
    ];

    for (const { name, db, key } of databases) {
        if (!db) continue;

        let controller = db[key];

        // Handle inheritance
        if (controller?.inherits) {
            const parent = db[controller.inherits];
            controller = { ...parent, ...controller, alarms: { ...parent?.alarms, ...controller.alarms } };
        }
        if (!controller || !controller.alarms) continue;

        // Try exact match
        let alarm = controller.alarms[normalizedCode];

        // Try without prefix (PS, SV, SP, OT, FE, MC, E, etc.)
        if (!alarm) {
            const codeWithoutPrefix = normalizedCode.replace(/^(PS|SV|SP|OT|IO|PW|OH|FE|MC|E|ATC)/, '');
            alarm = controller.alarms[codeWithoutPrefix];
        }
        // Try with common prefixes
        if (!alarm) {
            const prefixes = ['PS', 'SV', 'SP', 'OT', 'E', 'MC', 'FE'];
            for (const prefix of prefixes) {
                alarm = controller.alarms[prefix + normalizedCode];
                if (alarm) break;
            }
        }
        // Try fuzzy match
        if (!alarm) {
            const fuzzyMatch = Object.entries(controller.alarms).find(([code, data]) =>
                code.includes(normalizedCode) || normalizedCode.includes(code)
            );
            if (fuzzyMatch) {
                alarm = fuzzyMatch[1];
            }
        }
        if (alarm) {
            return {
                found: true,
                exactMatch: true,
                alarm: alarm,
                controller: controller.controllerName || controllerId,
                manufacturer: controller.manufacturer,
                servicePhone: controller.servicePhone,
                serviceEmail: controller.serviceEmail,
                servicePortal: controller.servicePortal,
                source: name
            };
        }
    }
    // Not found in any database
    return {
        found: false,
        message: `Alarm code ${normalizedCode} not found in any database`,
        suggestions: [
            "Check the exact alarm code from your control",
            "Try removing any prefix (PS, FE, MC, E)",
            "Include the full alarm message when contacting support",
            "Contact support@prism-ai.com for assistance"
        ]
    };
}
/**
 * Get all alarms for a specific category
 * @param {string} controllerId - Controller identifier
 * @param {string} category - Category ('g_code', 'm_code', 'option_required', 'hardware')
 * @returns {array} Array of alarms in that category
 */
function getAlarmsByCategory(controllerId, category) {
    const controller = MACHINE_ALARM_DATABASE[controllerId];
    if (!controller) return [];

    return Object.entries(controller.alarms)
        .filter(([code, alarm]) => alarm.category === category)
        .map(([code, alarm]) => ({ code, ...alarm }));
}
/**
 * Get recommended post processor changes for an alarm
 * @param {string} controllerId - Controller identifier
 * @param {string} alarmCode - Alarm code
 * @returns {array} Array of recommended post fixes
 */
function getPostFixesForAlarm(controllerId, alarmCode) {
    const result = lookupAlarmCode(controllerId, alarmCode);

    if (!result.found || !result.alarm.postFixes) {
        return [];
    }
    return result.alarm.postFixes.map(fix => ({
        ...fix,
        alarmCode: alarmCode,
        controller: result.controller
    }));
}
// Export
if (typeof module !== 'undefined' && module.exports) {
    module.exports = {
        MACHINE_ALARM_DATABASE,
        lookupAlarmCode,
        getAlarmsByCategory,
        getPostFixesForAlarm
    };
}
