/**
 * Get service provider for a machine
 * @param {string} manufacturer - Machine manufacturer
 * @param {string} state - US state abbreviation (optional)
 * @returns {object} Service provider information
 */
function getServiceProvider(manufacturer, state) {
    const mfr = manufacturer.toLowerCase();

    // Map manufacturer to OEM key
    const mfrMap = {
        'haas': 'haas',
        'okuma': 'okuma',
        'mazak': 'mazak',
        'dmg': 'dmgmori',
        'mori': 'dmgmori',
        'dmgmori': 'dmgmori',
        'hurco': 'hurco',
        'makino': 'makino',
        'hermle': 'hermle',
        'brother': 'brother',
        'doosan': 'doosan',
        'dn solutions': 'doosan',
        'fanuc': 'fanuc',
        'siemens': 'siemens',
        'heidenhain': 'heidenhain'
    };
    const oemKey = mfrMap[mfr] || mfr;
    const oem = SERVICE_PROVIDER_DATABASE.oem[oemKey];

    if (!oem) {
        return {
            found: false,
            message: `No service provider found for ${manufacturer}`,
            suggestion: "Contact your machine dealer or local machine tool service company"
        };
    }
    const result = {
        found: true,
        name: oem.name,
        mainPhone: oem.mainPhone,
        website: oem.website,
        regional: null
    };
    // Find regional contact if state provided
    if (state && oem.regions) {
        for (const [regionKey, region] of Object.entries(oem.regions)) {
            if (region.states && region.states.includes(state.toUpperCase())) {
                result.regional = {
                    name: region.name,
                    phone: region.phone,
                    locations: region.locations
                };
                break;
            }
        }
    }
    return result;
}
