    function calculateStability() {
        // Check if feature is available (SHOP tier)
        if (!hasFeature('workholding')) {
            return;
        }
        // Calculate stability
        const result = StabilityEngine.calculate();
        const score = result.score;

        // Update stability display
        const valueEl = document.getElementById('stabilityValue');
        const indicatorEl = document.getElementById('stabilityIndicator');

        if (valueEl) {
            valueEl.textContent = Math.round(score) + '%';
            valueEl.className = 'stability-value ' +
                (score >= 85 ? 'excellent' : score >= 70 ? 'good' : score >= 50 ? 'warning' : 'critical');
        }
        if (indicatorEl) {
            indicatorEl.style.left = score + '%';
        }
        // Update clamp percentage
        const stockHeight = parseFloat(document.getElementById('stockHeight')?.value) || 2;
        const clampedDepth = parseFloat(document.getElementById('clampedDepth')?.value) || 0.75;
        const clampPercentEl = document.getElementById('clampPercent');
        if (clampPercentEl) {
            clampPercentEl.textContent = `(${((clampedDepth/stockHeight)*100).toFixed(1)}%)`;
        }
        // Update warnings
        const warningsEl = document.getElementById('stabilityWarnings');
        if (warningsEl) {
            warningsEl.innerHTML = result.warnings.map(w => `
                <div class="stability-warning ${w.type}">${w.text}</div>
            `).join('');
        }
        // Calculate and display forces
        const forces = CuttingForceEngine.calculate({
            toolDia: parseFloat(document.getElementById('toolDia')?.value) || 0.5,
            flutes: parseInt(document.getElementById('flutes')?.value) || 4,
            rpm: parseFloat(document.getElementById('resultRpm')?.textContent?.replace(/,/g, '')) || 5000,
            feedrate: parseFloat(document.getElementById('resultFeed')?.textContent) || 50,
            doc: parseFloat(document.getElementById('doc')?.value) || 0.5,
            woc: parseFloat(document.getElementById('woc')?.value) || 0.25,
            material: 'steel_1018'
        });

        document.getElementById('forceTangential')?.textContent = forces.tangential.toFixed(0);
        document.getElementById('forceRadial')?.textContent = forces.radial.toFixed(0);
        document.getElementById('forceResultant')?.textContent = forces.resultant.toFixed(0);

        // Update recommendations
        const recs = StabilityEngine.getRecommendations(score);
        document.getElementById('suggestedDOC')?.textContent = recs.doc;
        document.getElementById('suggestedWOC')?.textContent = recs.woc;
        document.getElementById('suggestedStickout')?.textContent = recs.stickout;
        document.getElementById('suggestedStrategy')?.textContent = recs.strategy;
    }
