    /**
     * Export parts list
     */
    function exportPartsList() {
        const partsDb = getPartsForMachineType();
        let csv = 'Part Number,Name,Category,Price,Criticality,Lead Time,Life Expectancy,In Stock,Description\n';

        for (const [categoryKey, category] of Object.entries(partsDb)) {
            for (const [partId, part] of Object.entries(category.parts)) {
                csv += `"${partId}","${part.name}","${category.name}","$${part.price}","${part.criticality}","${part.leadTime}","${part.life || 'N/A'}","${part.inStock ? 'Yes' : 'No'}","${part.description}"\n`;
            }
        }
        const blob = new Blob([csv], { type: 'text/csv' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'machine_parts_list.csv';
        a.click();
        URL.revokeObjectURL(url);
    }
