    // MAIN CALCULATION FUNCTION
    // Calculate optimized feed for a given set of conditions

    /**
     * Calculate optimized feed rate using PRISM Roughing Logic
     * @param {object} params - Cutting parameters
     * @returns {object} Optimized feed with breakdown
     */
    calculateOptimizedFeed: function(params) {
        const {
            baseFeed,
            toolDiameter,
            radialEngagement,
            axialDepth,          // Current actual axial depth (LOC)
            programmedDepth,     // Maximum programmed axial depth (LOC)
            stickout,
            arcRadius,
            isConvexArc,
            directionChangeAngle,
            material,
            operationType,
            aggressivenessLevel,
            isPrismCalculatedFeed  // NEW: Flag to indicate feed came from PRISM Calculator
        } = params;

        // Start with base feed - this is the PRISM Calculator optimal feed
        let optimizedFeed = baseFeed;
        const adjustments = [];

        // IMPORTANT: When using PRISM Calculator feeds (default), we do NOT
        // apply aggressiveness reduction. The feed is already optimal.
        // We only apply DYNAMIC adjustments based on changing conditions.

        // 1. Apply aggressiveness level ONLY if explicitly requested
        //    (i.e., when using generic CAM feeds, not PRISM calculated feeds)
        const usePrismFeed = isPrismCalculatedFeed !== false; // Default to true
        if (!usePrismFeed && aggressivenessLevel) {
            const level = this.AGGRESSIVENESS_LEVELS[aggressivenessLevel || 5];
            if (level.factor < 1.0) {
                optimizedFeed *= level.factor;
                adjustments.push({
                    name: "Aggressiveness",
                    factor: level.factor,
                    description: level.name
                });
            }
        }
        // 2. Chip thinning compensation (INCREASE feed at light radial engagement)
        if (toolDiameter && radialEngagement) {
            const ctf = this.CHIP_THINNING.calculate(toolDiameter, radialEngagement);
            if (ctf > 1.0) {
                optimizedFeed *= ctf;
                adjustments.push({
                    name: "Chip Thinning (WOC)",
                    factor: ctf,
                    description: `+${((ctf - 1) * 100).toFixed(0)}% for ${((radialEngagement / toolDiameter) * 100).toFixed(0)}% stepover`
                });
            }
        }
        // 3. LENGTH OF CUT compensation (INCREASE feed when LOC is less than programmed)
        //    This is the PRIMARY adjustment for 3D Adaptive on tapered walls
        if (programmedDepth && axialDepth && axialDepth < programmedDepth) {
            const locResult = this.LENGTH_OF_CUT.calculate(programmedDepth, axialDepth);
            if (locResult.factor > 1.0) {
                optimizedFeed *= locResult.factor;
                adjustments.push({
                    name: "Length of Cut (LOC)",
                    factor: locResult.factor,
                    description: `+${((locResult.factor - 1) * 100).toFixed(0)}% for ${(axialDepth).toFixed(3)}" LOC (programmed: ${programmedDepth.toFixed(3)}")`
                });
            }
        }
        // 4. Arc feed correction (REDUCE feed in tight arcs for safety)
        if (arcRadius && toolDiameter) {
            const arcFactor = this.ARC_FEED.calculate(arcRadius, toolDiameter / 2, isConvexArc);
            if (arcFactor < 1.0) {
                optimizedFeed *= arcFactor;
                adjustments.push({
                    name: "Arc Correction",
                    factor: arcFactor,
                    description: `-${((1 - arcFactor) * 100).toFixed(0)}% for R${arcRadius.toFixed(3)}" arc`
                });
            }
        }
        // 5. Direction change (REDUCE feed at corners for safety)
        if (directionChangeAngle) {
            const cornerFactor = this.DIRECTION_CHANGE.calculate(directionChangeAngle);
            if (cornerFactor < 1.0) {
                optimizedFeed *= cornerFactor;
                adjustments.push({
                    name: "Corner Reduction",
                    factor: cornerFactor,
                    description: `-${((1 - cornerFactor) * 100).toFixed(0)}% for ${directionChangeAngle}° corner`
                });
            }
        }
        // 6. Tool stickout compensation (REDUCE feed for long tools)
        if (stickout && toolDiameter) {
            const stickoutFactor = this.STICKOUT_COMPENSATION.calculate(stickout, toolDiameter, operationType || 'roughing');
            if (stickoutFactor < 1.0) {
                optimizedFeed *= stickoutFactor;
                adjustments.push({
                    name: "Stickout Compensation",
                    factor: stickoutFactor,
                    description: `-${((1 - stickoutFactor) * 100).toFixed(0)}% for L/D ${(stickout / toolDiameter).toFixed(1)}`
                });
            }
        }
        // Calculate total adjustment factor
        const totalFactor = optimizedFeed / baseFeed;

        return {
            baseFeed: baseFeed,
            optimizedFeed: Math.round(optimizedFeed * 1000) / 1000,
            totalFactor: Math.round(totalFactor * 1000) / 1000,
            adjustments: adjustments,
            aggressivenessLevel: aggressivenessLevel || 5,
            aggressivenessName: level.name
        };
    },
    /**
     * Generate G-code comment block for PRISM Roughing Logic
     * @param {string} controllerId - Controller identifier
     * @param {object} params - Parameters for the block
     * @returns {string[]} Array of G-code lines
     */
    generateCodeBlock: function(controllerId, params) {
        const controller = this.CONTROLLER_IMPLEMENTATIONS[controllerId];
        if (!controller) {
            // Default format
            return [
                "(PRISM ROUGHING LOGIC ACTIVE)",
                `(LEVEL ${params.level || 5})`
            ];
        }
        return controller.generatePrismBlock(params);
    },
