    // CUTTING FORCE CALCULATION ENGINE - Kienzle Model
    const CuttingForceEngine = {
        // Material cutting force constants (kc1.1 in N/mm², mc exponent)
        materialConstants: {
            steel_1018: { kc11: 2100, mc: 0.25 },
            steel_4140: { kc11: 2400, mc: 0.23 },
            aluminum_6061: { kc11: 800, mc: 0.30 },
            aluminum_7075: { kc11: 950, mc: 0.28 },
            stainless_304: { kc11: 2800, mc: 0.21 },
            titanium: { kc11: 1900, mc: 0.18 },
            cast_iron: { kc11: 1450, mc: 0.28 }
        },
        // Calculate specific cutting force: kc = kc1.1 × h^(-mc)
        specificCuttingForce: function(kc11, mc, chipThickness) {
            if (chipThickness <= 0) return kc11;
            return kc11 * Math.pow(chipThickness, -mc);
        },
        // Calculate cutting forces
        calculate: function(params) {
            const { toolDia, flutes, rpm, feedrate, doc, woc, material } = params;

            // Get material constants (default to steel)
            const mat = this.materialConstants[material] || this.materialConstants.steel_1018;

            // Calculate chip thickness (mm)
            const fz = (feedrate / rpm / flutes) * 25.4;  // feed per tooth in mm
            const engagementRatio = Math.min(woc / toolDia, 1);
            const avgChipThickness = fz * engagementRatio * 0.7;  // Simplified average

            // Specific cutting force
            const kc = this.specificCuttingForce(mat.kc11, mat.mc, Math.max(avgChipThickness, 0.01));

            // Force calculations (simplified Kienzle)
            const docMM = doc * 25.4;
            const Fc = kc * docMM * avgChipThickness * flutes * engagementRatio;  // Tangential (N)
            const Fr = Fc * 0.4;  // Radial ≈ 40% of tangential
            const Fa = Fc * 0.2;  // Axial ≈ 20% of tangential
            const Ft = Math.sqrt(Fc*Fc + Fr*Fr + Fa*Fa);  // Resultant

            // Convert to lbf (1 N = 0.2248 lbf)
            return {
                tangential: Fc * 0.2248,
                radial: Fr * 0.2248,
                axial: Fa * 0.2248,
                resultant: Ft * 0.2248
            };
        }
    };
    // STABILITY CALCULATION ENGINE
    let selectedWorkholding = null;

    const StabilityEngine = {
        // Calculate overall stability score (0-100%)
        calculate: function() {
            // Get inputs
            const stockHeight = parseFloat(document.getElementById('stockHeight')?.value) || 2;
            const clampedDepth = parseFloat(document.getElementById('clampedDepth')?.value) || 0.75;
            const toolDia = parseFloat(document.getElementById('toolDia')?.value) || 0.5;
            const stickout = parseFloat(document.getElementById('stickout')?.value) || 2.0;
            const doc = parseFloat(document.getElementById('doc')?.value) || 0.5;
            const woc = parseFloat(document.getElementById('woc')?.value) || 0.25;

            // Get workholding
            const wh = selectedWorkholding ? WORKHOLDING_DATABASE[selectedWorkholding] : null;
            const baseRigidity = wh ? wh.rigidityScore : 7.0;

            // Calculate factors
            const clampRatio = clampedDepth / stockHeight;
            const stickoutRatio = stickout / toolDia;

            let score = 100;
            const warnings = [];

            // Clamping factor (20 points)
            if (clampRatio < 0.25) {
                score -= 25;
                warnings.push({ type: 'critical', text: '⚠️ CRITICAL: Clamping too shallow - ejection risk!' });
            } else if (clampRatio < 0.33) {
                score -= 12;
                warnings.push({ type: 'caution', text: '⚠️ Low clamping ratio - reduce aggressiveness' });
            }
            // Stickout factor (20 points)
            if (stickoutRatio > 5) {
                score -= 20;
                warnings.push({ type: 'critical', text: '⚠️ Extreme stickout - severe deflection!' });
            } else if (stickoutRatio > 4) {
                score -= 10;
                warnings.push({ type: 'caution', text: '⚠️ High stickout - reduce DOC/feed' });
            }
            // Workholding rigidity factor (15 points)
            score -= (10 - baseRigidity) * 1.5;

            // Cutting aggressiveness (15 points)
            const aggressiveness = (doc * woc) / (toolDia * toolDia);
            if (aggressiveness > 0.3) {
                score -= 15;
            } else if (aggressiveness > 0.15) {
                score -= 8;
            }
            return {
                score: Math.max(0, Math.min(100, score)),
                warnings: warnings,
                clampRatio: clampRatio,
                stickoutRatio: stickoutRatio
            };
        },
        // Get recommended parameters based on stability
        getRecommendations: function(score) {
            const toolDia = parseFloat(document.getElementById('toolDia')?.value) || 0.5;

            if (score >= 85) {
                return {
                    doc: (toolDia * 2.0).toFixed(3) + '"',
                    woc: (toolDia * 0.25).toFixed(3) + '"',
                    stickout: (toolDia * 4).toFixed(2) + '"',
                    strategy: 'Adaptive/Trochoidal'
                };
            } else if (score >= 70) {
                return {
                    doc: (toolDia * 1.5).toFixed(3) + '"',
                    woc: (toolDia * 0.20).toFixed(3) + '"',
                    stickout: (toolDia * 3.5).toFixed(2) + '"',
                    strategy: 'Adaptive Clearing'
                };
            } else if (score >= 50) {
                return {
                    doc: (toolDia * 1.0).toFixed(3) + '"',
                    woc: (toolDia * 0.15).toFixed(3) + '"',
                    stickout: (toolDia * 3).toFixed(2) + '"',
                    strategy: 'Light Pocketing'
                };
            } else {
                return {
                    doc: (toolDia * 0.5).toFixed(3) + '"',
                    woc: (toolDia * 0.10).toFixed(3) + '"',
                    stickout: (toolDia * 2.5).toFixed(2) + '"',
                    strategy: 'Conservative'
                };
            }
        }
    };
