MACHINE_DATABASE.getMachine = function(machineId) { return this.machines[machineId] || null; };
MACHINE_DATABASE.getSpindleGeometry = function(taperType) { return this.spindleGeometry[taperType] || null; };
MACHINE_DATABASE.getKinematicModel = function(kinematicType) { return this.machineKinematics[kinematicType] || null; };
MACHINE_DATABASE.getCollisionConfig = function(machineId) {
    const machine = this.machines[machineId];
    if (!machine || !machine.collisionConfig) return null;
    const config = machine.collisionConfig;
    return { machine: machineId, spindle: this.spindleGeometry[config.spindleGeometry], kinematics: this.machineKinematics[config.kinematicType], activeZones: config.activeZones.map(z => ({ name: z, ...this.collisionZones[z] })), workEnvelope: machine.workEnvelope, tableSurface: machine.tableSurface };
};
MACHINE_DATABASE.listByManufacturer = function(mfr) { return Object.entries(this.machines).filter(([id, m]) => m.manufacturer === mfr).map(([id, m]) => ({ id, ...m })); };
MACHINE_DATABASE.listByType = function(type) { return Object.entries(this.machines).filter(([id, m]) => m.type && m.type.toLowerCase().includes(type.toLowerCase())).map(([id, m]) => ({ id, ...m })); };
MACHINE_DATABASE.listByTaper = function(taper) { return Object.entries(this.machines).filter(([id, m]) => m.spindle?.taper?.toLowerCase().includes(taper.toLowerCase())).map(([id, m]) => ({ id, ...m })); };
MACHINE_DATABASE.checkSpindleClearance = function(machineId, zPos, stockTop) { const m = this.machines[machineId]; if (!m) return { error: 'Machine not found' }; const s = this.spindleGeometry[m.collisionConfig?.spindleGeometry]; if (!s) return { error: 'Spindle not found' }; const h = s.collisionEnvelope?.height || 150; const c = zPos - stockTop - h; return { clear: c > 0, clearance: c, minSafeZ: stockTop + h + 10, headHeight: h }; };
MACHINE_DATABASE.calculateSafeRapidZ = function(machineId, stockTop, toolProj) { const m = this.machines[machineId]; if (!m) return stockTop + 100; const s = this.spindleGeometry[m.collisionConfig?.spindleGeometry]; const h = s?.collisionEnvelope?.height || 150; const k = this.machineKinematics[m.collisionConfig?.kinematicType]; const c = k?.typicalClearance?.z || 100; return stockTop + toolProj + c; };
MACHINE_DATABASE.checkToolFit = function(machineId, toolLen, toolDia, stockH) { const m = this.machines[machineId]; if (!m) return { error: 'Machine not found' }; const z = m.workEnvelope?.maxZ || 400; const req = stockH + toolLen + 50; return { fits: z >= req, zTravel: z, required: req, margin: z - req }; };
MACHINE_DATABASE.getTableSurfaceArea = function(machineId) { return this.machines[machineId]?.tableSurface || null; };

