    // SECONDARY OPERATIONS CALCULATOR FUNCTIONS

    /**
     * Calculate recommended stock allowance for secondary operations
     * @param {Object} params - Parameters for calculation
     * @returns {Object} - Recommended allowances and notes
     */
    function calculateSecondaryOpStock(params) {
        const {
            material = 'tool_steel_air',
            heatTreatment = null,
            finishingOp = null,
            nominalDimension = 1.0,  // inches
            toleranceClass = 'standard',  // tight, standard, loose
            featureType = 'od',  // od, id, flat, profile
            isMetric = false
        } = params;

        if (!heatTreatment && !finishingOp) {
            return null;  // No secondary ops selected
        }
        const db = SECONDARY_OPERATIONS_DATABASE;
        const materialData = db.materialFactors[material] || db.materialFactors.tool_steel_air;

        let results = {
            totalAllowance: 0,
            perSide: 0,
            breakdown: [],
            recommendations: [],
            warnings: [],
            targetDimension: nominalDimension,
            cutDimension: nominalDimension
        };
        // Tolerance multipliers
        const toleranceMultipliers = {
            tight: 0.8,     // Less stock, tighter control
            standard: 1.0,
            loose: 1.2      // More stock for safety
        };
        const tolMult = toleranceMultipliers[toleranceClass] || 1.0;

        // Calculate heat treatment distortion allowance
        if (heatTreatment && db.heatTreatment[heatTreatment]) {
            const htData = db.heatTreatment[heatTreatment];
            const growth = htData.distortion.linear_growth.typical * nominalDimension;
            const volumeGrowth = (htData.distortion.volumetric.typical / 100) * nominalDimension;

            // Total expected movement
            const expectedMovement = (growth + volumeGrowth * 0.3) * materialData.hardeningDistortion;

            results.breakdown.push({
                operation: htData.name,
                type: 'heat_treatment',
                allowance: expectedMovement,
                notes: htData.distortion.notes
            });

            results.recommendations.push(`${htData.name}: Expect ${(expectedMovement * 1000).toFixed(1)} thou growth per inch`);

            // Add to total
            results.totalAllowance += expectedMovement * 2;  // Both sides grow
        }
        // Calculate finishing operation stock requirement
        if (finishingOp && db.finishingOperations[finishingOp]) {
            const finishData = db.finishingOperations[finishingOp];
            let stockNeeded = finishData.stockRequired.recommended * materialData.stockMultiplier * tolMult;

            // Adjust for feature type
            if (featureType === 'id') {
                stockNeeded *= 1.1;  // ID needs slightly more stock
            } else if (featureType === 'profile') {
                stockNeeded *= 1.2;  // Profiles need more stock
            }
            results.breakdown.push({
                operation: finishData.name,
                type: 'finishing',
                allowance: stockNeeded,
                perSide: finishData.stockRequired.perSide,
                capabilities: finishData.capabilities,
                notes: finishData.stockRequired.notes
            });

            const stockThou = (stockNeeded * 1000).toFixed(1);
            results.recommendations.push(`${finishData.name}: Leave ${stockThou} thou per side (${(stockNeeded * 2 * 1000).toFixed(1)} total on diameter)`);

            // Add achievable tolerance info
            const achTol = (finishData.capabilities.tolerance.achievable * 1000).toFixed(2);
            results.recommendations.push(`Achievable tolerance: ±${achTol} thou`);

            results.totalAllowance += stockNeeded * 2;  // Both sides
            results.perSide = stockNeeded;
        }
        // Calculate cut dimension based on feature type
        if (featureType === 'od' || featureType === 'flat') {
            // For OD/flat: cut larger, grind down
            results.cutDimension = nominalDimension + results.totalAllowance;
            results.cutInstruction = `Cut OVERSIZE: ${formatDimension(results.cutDimension, isMetric)}`;
        } else if (featureType === 'id') {
            // For ID: cut smaller, grind/hone to size
            results.cutDimension = nominalDimension - results.totalAllowance;
            results.cutInstruction = `Cut UNDERSIZE: ${formatDimension(results.cutDimension, isMetric)}`;
        } else if (featureType === 'profile') {
            // For profiles: offset by stock allowance
            results.cutDimension = results.perSide;
            results.cutInstruction = `Offset path by: ${formatDimension(results.perSide, isMetric)}`;
        }
        // Add warnings for common issues
        if (heatTreatment === 'case_hardening' && finishingOp !== 'cylindrical_grinding' && finishingOp !== 'internal_grinding') {
            results.warnings.push("Case hardened parts typically require grinding to finish dimension");
        }
        if (results.totalAllowance > 0.050) {
            results.warnings.push("Large stock allowance - consider rough grinding before heat treat");
        }
        if (finishingOp === 'honing' && !heatTreatment) {
            results.recommendations.push("Honing follows bore centerline - ensure location is correct before honing");
        }
        // Add cost estimate factor
        if (db.finishingOperations[finishingOp]) {
            results.costFactor = db.finishingOperations[finishingOp].costFactor;
        }
        // Convert to metric if needed
        if (isMetric) {
            results.totalAllowance *= 25.4;
            results.perSide *= 25.4;
            results.cutDimension *= 25.4;
            results.breakdown.forEach(b => b.allowance *= 25.4);
        }
        return results;
    }
    /**
     * Format dimension with appropriate precision
     */
    function formatDimension(value, isMetric) {
        if (isMetric) {
            return value.toFixed(3) + ' mm';
        } else {
            return value.toFixed(4) + '"';
        }
    }
    /**
     * Get recommended finishing operation for given requirements
     */
    function recommendFinishingOp(params) {
        const { toleranceRequired, surfaceFinish, featureType, isHardened } = params;
        const db = SECONDARY_OPERATIONS_DATABASE.finishingOperations;

        let candidates = [];

        for (const [key, op] of Object.entries(db)) {
            if (op.capabilities.tolerance.achievable <= toleranceRequired) {
                candidates.push({
                    key,
                    name: op.name,
                    cost: op.costFactor,
                    tolerance: op.capabilities.tolerance.achievable,
                    finish: op.capabilities.surfaceFinish?.achievable
                });
            }
        }
        // Sort by cost (lowest first)
        candidates.sort((a, b) => a.cost - b.cost);

        return candidates[0] || null;
    }
