    /**
     * Get parts database based on current machine mode
     */
    function getPartsForMachineType() {
        const mode = typeof machineMode !== 'undefined' ? machineMode : 'mill';

        switch (mode) {
            case 'mill': return MACHINE_PARTS_DATABASE.mill;
            case 'lathe': return MACHINE_PARTS_DATABASE.lathe;
            case 'wire_edm': return MACHINE_PARTS_DATABASE.wire_edm;
            case 'edm': return MACHINE_PARTS_DATABASE.sinker_edm;
            case 'laser': return MACHINE_PARTS_DATABASE.laser;
            case 'waterjet': return MACHINE_PARTS_DATABASE.waterjet;
            default: return MACHINE_PARTS_DATABASE.mill;
        }
    }
    /**
     * Load parts for the current machine type
     */
    function loadPartsForCurrentMachine() {
        const partsDb = getPartsForMachineType();
        renderPartsGrid(partsDb);
        updatePartsCount(partsDb);
    }
    /**
     * Select a parts category tab
     */
    function selectPartsCategory(category) {
        currentPartsCategory = category;

        // Update tab styling
        document.querySelectorAll('.parts-tab').forEach(tab => {
            tab.classList.remove('active');
            if (tab.dataset.category === category) {
                tab.classList.add('active');
            }
        });

        filterParts();
    }
    /**
     * Filter and render parts based on search and category
     */
    function filterParts() {
        const searchInput = document.getElementById('partsSearchInput');
        const sortBy = document.getElementById('partsSortBy');
        const inStockOnly = document.getElementById('partsInStockOnly');

        const searchTerm = searchInput ? searchInput.value.toLowerCase() : '';
        const sortOrder = sortBy ? sortBy.value : 'name';
        const stockFilter = inStockOnly ? inStockOnly.checked : false;

        const partsDb = getPartsForMachineType();
        let filteredParts = [];

        // Collect all parts
        for (const [categoryKey, category] of Object.entries(partsDb)) {
            if (currentPartsCategory !== 'all' && categoryKey !== currentPartsCategory) continue;

            for (const [partId, part] of Object.entries(category.parts)) {
                // Apply filters
                if (stockFilter && !part.inStock) continue;

                if (searchTerm) {
                    const matchesSearch =
                        part.name.toLowerCase().includes(searchTerm) ||
                        partId.toLowerCase().includes(searchTerm) ||
                        (part.description && part.description.toLowerCase().includes(searchTerm));
                    if (!matchesSearch) continue;
                }
                filteredParts.push({
                    id: partId,
                    ...part,
                    categoryKey,
                    categoryName: category.name,
                    categoryIcon: category.icon
                });
            }
        }
        // Sort parts
        filteredParts.sort((a, b) => {
            switch (sortOrder) {
                case 'price_low': return a.price - b.price;
                case 'price_high': return b.price - a.price;
                case 'category': return a.categoryName.localeCompare(b.categoryName);
                case 'criticality':
                    const critOrder = { high: 0, medium: 1, low: 2 };
                    return critOrder[a.criticality] - critOrder[b.criticality];
                default: return a.name.localeCompare(b.name);
            }
        });

        renderFilteredParts(filteredParts);
    }
    /**
     * Render the parts grid from database
     */
    function renderPartsGrid(partsDb) {
        const grid = document.getElementById('partsGrid');
        const emptyState = document.getElementById('partsEmptyState');
        if (!grid) return;

        let allParts = [];

        for (const [categoryKey, category] of Object.entries(partsDb)) {
            for (const [partId, part] of Object.entries(category.parts)) {
                allParts.push({
                    id: partId,
                    ...part,
                    categoryKey,
                    categoryName: category.name,
                    categoryIcon: category.icon
                });
            }
        }
        renderFilteredParts(allParts);
    }
    /**
     * Render filtered parts array
     */
    function renderFilteredParts(parts) {
        const grid = document.getElementById('partsGrid');
        const emptyState = document.getElementById('partsEmptyState');
        const countSpan = document.getElementById('partsResultCount');

        if (!grid) return;

        if (parts.length === 0) {
            grid.innerHTML = '';
            if (emptyState) emptyState.style.display = 'block';
            if (countSpan) countSpan.textContent = '0';
            return;
        }
        if (emptyState) emptyState.style.display = 'none';
        if (countSpan) countSpan.textContent = parts.length;

        grid.innerHTML = parts.map(part => {
            const inCart = partsCart.some(p => p.id === part.id);
            const critClass = `criticality-${part.criticality}`;

            return `
                <div class="part-card ${inCart ? 'in-cart' : ''}" data-part-id="${part.id}">
                    <div style="display: flex; justify-content: space-between; align-items: flex-start; margin-bottom: 10px;">
                        <div>
                            <div style="font-size: 10px; color: var(--text-muted); display: flex; align-items: center; gap: 4px;">
                                <span>${part.categoryIcon}</span>
                                <span>${part.categoryName}</span>
                            </div>
                            <div style="font-size: 9px; color: #888; font-family: monospace;">${part.id}</div>
                        </div>
                        <div style="text-align: right;">
                            <div class="${critClass}" style="font-size: 9px; font-weight: 600; text-transform: uppercase;">
                                ${part.criticality === 'high' ? '⚠️ Critical' : part.criticality === 'medium' ? '⚡ Medium' : '✓ Low'}
                            </div>
                            <div style="font-size: 9px; color: ${part.inStock ? '#22c55e' : '#ef4444'};">
                                ${part.inStock ? '● In Stock' : '○ Lead Time: ' + part.leadTime}
                            </div>
                        </div>
                    </div>

                    <div style="font-size: 13px; font-weight: 600; color: var(--text); margin-bottom: 6px; line-height: 1.3;">${part.name}</div>
                    <div style="font-size: 10px; color: var(--text-muted); margin-bottom: 12px; line-height: 1.4;">${part.description}</div>

                    <div style="display: flex; justify-content: space-between; align-items: center; padding-top: 10px; border-top: 1px solid var(--border);">
                        <div>
                            <div style="font-size: 18px; font-weight: 700; color: #22c55e;">$${part.price.toLocaleString()}</div>
                            ${part.life ? `<div style="font-size: 9px; color: var(--text-muted);">Life: ${part.life}</div>` : ''}
                        </div>
                        <div style="display: flex; gap: 6px;">
                            <button onclick="openPartBuyModal('${part.id}')" style="padding: 8px 12px; border-radius: 6px; font-size: 10px; font-weight: 600; cursor: pointer; background: var(--primary); border: none; color: #000;">
                                🛒 Buy
                            </button>
                            <button onclick="togglePartInCart('${part.id}')" style="padding: 8px 14px; border-radius: 6px; font-size: 11px; font-weight: 600; cursor: pointer; transition: all 0.2s; ${inCart ? 'background: #22c55e; border: none; color: #000;' : 'background: transparent; border: 1px solid #fb923c; color: #fb923c;'}">
                                ${inCart ? '✓' : '+'}
                            </button>
                        </div>
                    </div>
                </div>
            `;
        }).join('');
    }
    /**
     * Update parts count display
     */
    function updatePartsCount(partsDb) {
        let count = 0;
        for (const category of Object.values(partsDb)) {
            count += Object.keys(category.parts).length;
        }
        const countSpan = document.getElementById('partsResultCount');
        if (countSpan) countSpan.textContent = count;
    }
    /**
     * Toggle part in cart
     */
    function togglePartInCart(partId) {
        const existingIndex = partsCart.findIndex(p => p.id === partId);

        if (existingIndex >= 0) {
            partsCart.splice(existingIndex, 1);
        } else {
            // Find the part in the database
            const partsDb = getPartsForMachineType();
            for (const category of Object.values(partsDb)) {
                if (category.parts[partId]) {
                    partsCart.push({
                        id: partId,
                        ...category.parts[partId],
                        qty: 1
                    });
                    break;
                }
            }
        }
        updateCartDisplay();
        filterParts(); // Re-render to update button states
    }
    /**
     * Open buy modal for a machine part
     */
    function openPartBuyModal(partId) {
        const partsDb = getPartsForMachineType();
        let foundPart = null;
        let categoryName = '';

        for (const [catKey, category] of Object.entries(partsDb)) {
            if (category.parts[partId]) {
                foundPart = { id: partId, ...category.parts[partId] };
                categoryName = category.name;
                break;
            }
        }
        if (!foundPart) return;

        openPrismBuyModal({
            name: foundPart.name,
            partNumber: partId,
            brand: 'OEM Part',
            category: categoryName,
            price: foundPart.price,
            type: 'machine_part',
            inStock: foundPart.inStock,
            leadTime: foundPart.leadTime,
            description: foundPart.description
        });
    }
    /**
     * Update cart display
     */
    function updateCartDisplay() {
        const cartBar = document.getElementById('partsCartBar');
        const cartCount = document.getElementById('partsCartCount');
        const cartSubtotal = document.getElementById('partsCartSubtotal');

        if (partsCart.length === 0) {
            if (cartBar) cartBar.style.display = 'none';
            return;
        }
        if (cartBar) cartBar.style.display = 'block';
        if (cartCount) cartCount.textContent = partsCart.length;

        const subtotal = partsCart.reduce((sum, p) => sum + (p.price * p.qty), 0);
        if (cartSubtotal) cartSubtotal.textContent = '$' + subtotal.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 });
    }
    /**
     * View parts cart
     */
    function viewPartsCart() {
        // For now, show a simple alert with cart contents
        if (partsCart.length === 0) {
            alert('Your cart is empty');
            return;
        }
        let cartSummary = 'PARTS CART\n' + '='.repeat(40) + '\n\n';
        let total = 0;

        partsCart.forEach(part => {
            cartSummary += `${part.name}\n`;
            cartSummary += `  Part #: ${part.id}\n`;
            cartSummary += `  Price: $${part.price.toLocaleString()}\n`;
            cartSummary += `  Qty: ${part.qty}\n\n`;
            total += part.price * part.qty;
        });

        cartSummary += '='.repeat(40) + '\n';
        cartSummary += `SUBTOTAL: $${total.toLocaleString(undefined, { minimumFractionDigits: 2 })}\n`;
        cartSummary += '\nNote: Prices are estimates. Contact your dealer for exact pricing and availability.';

        alert(cartSummary);
    }
