    // Toggle CAM panel visibility
    function toggleCamPanel() {
        const content = document.getElementById('camPanelContent');
        const btn = document.getElementById('camToggleBtn');
        if (content.style.display === 'none') {
            content.style.display = 'block';
            btn.textContent = '▲ Collapse';
            updateToolpathRecommendations();
        } else {
            content.style.display = 'none';
            btn.textContent = '▼ Expand';
        }
    }
    // PROGRAMMING APPROACH HANDLER
    function updateProgrammingApproach() {
        const approach = document.getElementById('programmingApproach')?.value || 'cam_software';

        // Hide all sections first
        document.getElementById('camSoftwareSection')?.style.display = 'none';
        document.getElementById('manualProgrammingSection')?.style.display = 'none';
        document.getElementById('cannedCyclesSection')?.style.display = 'none';
        document.getElementById('conversationalSection')?.style.display = 'none';

        // Hide/show CAM-specific panels
        const quickStrategyPanel = document.getElementById('quickStrategyPanel');
        const machineCompatibility = document.getElementById('machineCompatibility');
        const materialTips = document.getElementById('materialTips');
        const toolpathCategoryBtns = document.querySelector('.form-group:has(#toolpathCategoryBtns)') ||
                                      document.getElementById('toolpathCategoryBtns')?.parentElement;
        const toolpathList = document.querySelector('.form-group:has(#toolpathList)') ||
                             document.getElementById('toolpathList')?.parentElement;
        const selectedToolpathPanel = document.getElementById('selectedToolpathPanel');

        if (approach === 'cam_software') {
            document.getElementById('camSoftwareSection')?.style.display = 'block';
            if (quickStrategyPanel) quickStrategyPanel.style.display = 'block';
            if (machineCompatibility) machineCompatibility.style.display = 'block';
            if (materialTips) materialTips.style.display = 'block';
            if (toolpathCategoryBtns) toolpathCategoryBtns.style.display = 'block';
            if (toolpathList) toolpathList.style.display = 'block';
            updateCamToolpaths();
        } else {
            if (quickStrategyPanel) quickStrategyPanel.style.display = 'none';
            if (machineCompatibility) machineCompatibility.style.display = 'none';
            if (materialTips) materialTips.style.display = 'none';
            if (toolpathCategoryBtns) toolpathCategoryBtns.style.display = 'none';
            if (toolpathList) toolpathList.style.display = 'none';
            if (selectedToolpathPanel) selectedToolpathPanel.style.display = 'none';

            if (approach === 'manual_hardcode') {
                document.getElementById('manualProgrammingSection')?.style.display = 'block';
                updateManualProgrammingGuide();
            } else if (approach === 'canned_cycles') {
                document.getElementById('cannedCyclesSection')?.style.display = 'block';
                updateCannedCycleGuide();
            } else if (approach === 'conversational') {
                document.getElementById('conversationalSection')?.style.display = 'block';
                updateConversationalGuide();
            }
        }
    }
    // MANUAL G-CODE PROGRAMMING GUIDES
    // CONTROL TYPE DETECTION FROM SELECTED MACHINE
    function getSelectedControlType() {
        const machineSelect = document.getElementById('machineSelect');
        const manufacturerSelect = document.getElementById('machineManufacturer');

        if (!machineSelect || !manufacturerSelect) return 'fanuc'; // Default to Fanuc

        const machineValue = machineSelect.value?.toLowerCase() || '';
        const mfr = manufacturerSelect.value?.toLowerCase() || '';

        // Detect control type from manufacturer/machine
        if (mfr.includes('haas') || machineValue.includes('haas') || machineValue.includes('vf-') || machineValue.includes('umc-') || machineValue.includes('st-')) {
            return 'haas';
        }
        if (mfr.includes('mazak') || machineValue.includes('mazak') || machineValue.includes('integrex') || machineValue.includes('variaxis')) {
            return 'mazak';
        }
        if (mfr.includes('okuma') || machineValue.includes('okuma') || machineValue.includes('genos') || machineValue.includes('multus')) {
            return 'okuma';
        }
        if (mfr.includes('siemens') || machineValue.includes('sinumerik') || machineValue.includes('840d')) {
            return 'siemens';
        }
        if (mfr.includes('heidenhain') || machineValue.includes('heidenhain') || machineValue.includes('tnc')) {
            return 'heidenhain';
        }
        if (mfr.includes('hurco') || machineValue.includes('hurco')) {
            return 'hurco';
        }
        if (mfr.includes('dmg') || mfr.includes('mori') || machineValue.includes('dmg') || machineValue.includes('ntx') || machineValue.includes('ctx')) {
            return 'dmg_mori';
        }
        if (mfr.includes('doosan') || mfr.includes('dn solutions') || machineValue.includes('puma') || machineValue.includes('dnm')) {
            return 'fanuc'; // Doosan uses Fanuc
        }
        if (mfr.includes('brother') || machineValue.includes('speedio')) {
            return 'brother';
        }
        if (mfr.includes('makino') || machineValue.includes('makino')) {
            return 'fanuc'; // Makino typically uses Fanuc-based
        }
        // Default to Fanuc (most common)
        return 'fanuc';
    }
