    // Get material category for toolpath recommendations
    function getMaterialCategory() {
        const groupId = document.getElementById('materialGroup')?.value || '';

        if (groupId.startsWith('N_aluminum') || groupId === 'N_magnesium') return 'aluminum';
        if (groupId.startsWith('P_tool')) return 'tool_steel';
        if (groupId.startsWith('P_')) return 'steel';
        if (groupId.startsWith('M_')) return 'stainless';
        if (groupId === 'S_titanium') return 'titanium';
        if (groupId === 'S_nickel' || groupId === 'S_cobalt') return 'superalloy';
        if (groupId.startsWith('K_')) return 'cast_iron';
        if (groupId.startsWith('H_')) return 'hardened';
        if (groupId.startsWith('O_plastic')) return 'plastic';
        if (groupId === 'O_composite') return 'composite';
        if (groupId === 'O_graphite') return 'graphite';

        return 'steel'; // Default
    }
