    /**
     * Get lathe material category from current selection
     */
    function getLatheMatCategory() {
        const materialGroup = document.getElementById('materialGroup')?.value || '';

        if (materialGroup.includes('aluminum') || materialGroup.includes('N_')) {
            return 'aluminum';
        } else if (materialGroup.includes('stainless') || materialGroup.includes('M_')) {
            return 'stainless';
        } else if (materialGroup.includes('cast') || materialGroup.includes('K_')) {
            return 'cast_iron';
        } else if (materialGroup.includes('titanium')) {
            return 'titanium';
        } else if (materialGroup.includes('nickel') || materialGroup.includes('inconel') || materialGroup.includes('hastelloy')) {
            return 'superalloys';
        } else {
            return 'steel';
        }
    }
    // POST PROCESSOR OPTIMIZATION SYSTEM

    let uploadedPostProcessor = null;
    let postAnalysisComplete = false;

    /**
     * Handle file drop on post processor upload zone
     */
    function handlePostDrop(event) {
        event.preventDefault();
        const dropZone = document.getElementById('postUploadZone');
        dropZone.style.borderColor = 'rgba(124, 58, 237, 0.4)';
        dropZone.style.background = 'rgba(124, 58, 237, 0.05)';

        const files = event.dataTransfer.files;
        if (files.length > 0) {
            processPostFile(files[0]);
        }
    }
    /**
     * Handle file input change for post processor upload
     */
    function handlePostUpload(event) {
        const files = event.target.files;
        if (files.length > 0) {
            processPostFile(files[0]);
        }
    }
    /**
     * Process uploaded post processor file
     */
    function processPostFile(file) {
        const validExtensions = ['.cps', '.post', '.lua', '.txt', '.js'];
        const ext = '.' + file.name.split('.').pop().toLowerCase();

        if (!validExtensions.includes(ext)) {
            alert('Please upload a valid post processor file (.cps, .post, .lua, .txt, .js)');
            return;
        }
        const reader = new FileReader();
        reader.onload = function(e) {
            uploadedPostProcessor = {
                name: file.name,
                size: file.size,
                content: e.target.result,
                extension: ext
            };
            // Show uploaded file info
            document.getElementById('uploadedPostInfo')?.style.display = 'block';
            document.getElementById('uploadedPostName')?.textContent = file.name;
            document.getElementById('uploadedPostSize')?.textContent = formatFileSize(file.size);

            // Update status
            document.getElementById('postStatus')?.innerHTML = '<span style="color: var(--success);">✓ Post uploaded</span>';

            // Analyze the post processor
            analyzePostProcessor();

            // Unlock the analysis section
            unlockPostAnalysis();

            // Enable purchase card
            enablePostPurchase();
        };
        reader.readAsText(file);
    }
    /**
     * Format file size for display
     */
    function formatFileSize(bytes) {
        if (bytes < 1024) return bytes + ' B';
        if (bytes < 1024 * 1024) return (bytes / 1024).toFixed(1) + ' KB';
        return (bytes / (1024 * 1024)).toFixed(1) + ' MB';
    }
    /**
     * Clear uploaded post processor
     */
    function clearUploadedPost() {
        uploadedPostProcessor = null;
        postAnalysisComplete = false;

        document.getElementById('uploadedPostInfo')?.style.display = 'none';
        document.getElementById('postStatus')?.textContent = 'No post processor uploaded';
        document.getElementById('postFileInput')?.value = '';

        // Lock the analysis section again
        document.getElementById('postLockOverlay')?.style.display = 'flex';

        // Disable purchase card
        const purchaseCard = document.getElementById('postPurchaseCard');
        purchaseCard.style.opacity = '0.5';
        purchaseCard.style.pointerEvents = 'none';

        // Reset analysis fields
        document.getElementById('postDetectedFormat')?.textContent = '--';
        document.getElementById('postLineCount')?.textContent = '--';
        document.getElementById('postCamSoftware')?.textContent = '--';
        document.getElementById('postOptScore')?.textContent = '--';
        document.getElementById('postOpt1')?.textContent = 'Upload post to analyze...';
        document.getElementById('postOpt2')?.textContent = '--';
        document.getElementById('postOpt3')?.textContent = '--';
        document.getElementById('postSavingTime')?.textContent = '--';
        document.getElementById('postSavingCode')?.textContent = '--';
        document.getElementById('postSavingFeatures')?.textContent = '--';
    }
    /**
     * Analyze uploaded post processor and detect features
     */
    function analyzePostProcessor() {
        if (!uploadedPostProcessor) return;

        const content = uploadedPostProcessor.content;
        const lines = content.split('\n');
        const lineCount = lines.length;

        // Detect format and CAM software
        let format = 'Unknown';
        let camSoftware = 'Unknown';

        if (content.includes('description') && content.includes('function onOpen')) {
            format = 'Fusion 360 / HSM';
            camSoftware = 'Autodesk Fusion 360';
        } else if (content.includes('pOutput') || content.includes('MastercamPost')) {
            format = 'Mastercam';
            camSoftware = 'Mastercam';
        } else if (content.includes('ESPRIT') || content.includes('esprit')) {
            format = 'ESPRIT';
            camSoftware = 'ESPRIT';
        } else if (content.includes('GibbsCAM') || content.includes('gibbscam')) {
            format = 'GibbsCAM';
            camSoftware = 'GibbsCAM';
        } else if (content.includes('SolidCAM') || content.includes('solidcam')) {
            format = 'SolidCAM';
            camSoftware = 'SolidCAM';
        } else if (content.includes('CAMWorks') || content.includes('camworks')) {
            format = 'CAMWorks';
            camSoftware = 'CAMWorks';
        } else if (content.includes('NX') || content.includes('UGPOST')) {
            format = 'Siemens NX';
            camSoftware = 'NX CAM';
        } else if (content.includes('Edgecam') || content.includes('edgecam')) {
            format = 'Edgecam';
            camSoftware = 'Edgecam';
        } else if (uploadedPostProcessor.extension === '.lua') {
            format = 'Lua Script';
            camSoftware = 'Generic Lua';
        } else if (uploadedPostProcessor.extension === '.cps') {
            format = 'Autodesk CPS';
            camSoftware = 'Autodesk CAM';
        }
        // Comprehensive analysis using POST_PROCESSOR_DATABASE
        let score = 100;
        let optimizations = [];

        // === HIGH-SPEED MACHINING ANALYSIS ===
        const hasHSM = content.includes('G05.1') || content.includes('G05 P') ||
                       content.includes('CYCLE832') || content.includes('G187') ||
                       content.includes('G131') || content.includes('COMPON');
        if (!hasHSM) {
            score -= 15;
            optimizations.push({ level: 'error', text: 'Missing high-speed machining (HSM) codes - up to 30% slower toolpaths' });
        }
        // === CHIP THINNING COMPENSATION ===
        const hasChipThinning = content.includes('chipThinning') || content.includes('chipLoad') ||
                                content.includes('radialEngagement') || content.includes('CTF');
        if (!hasChipThinning) {
            score -= 12;
            optimizations.push({ level: 'error', text: 'No chip thinning compensation - incorrect feeds at light engagement' });
        }
        // === DYNAMIC FEED ADJUSTMENT ===
        const hasDynamicFeed = content.includes('dynamicDepth') || content.includes('adaptiveFeed') ||
                               content.includes('variableFeed') || content.includes('prismRoughing');
        if (!hasDynamicFeed) {
            score -= 10;
            optimizations.push({ level: 'warning', text: 'No dynamic depth feed adjustment - adaptive toolpaths run slower' });
        }
        // === 5-AXIS TCP CONTROL ===
        const hasTCP = content.includes('G43.4') || content.includes('G43.5') ||
                       content.includes('TRAORI') || content.includes('G169') ||
                       content.includes('TCPM') || content.includes('G234');
        if (!hasTCP && content.includes('5') && content.includes('axis')) {
            score -= 10;
            optimizations.push({ level: 'warning', text: 'No TCP control for 5-axis operations' });
        }
        // === COOLANT CONTROL ===
        const hasTSC = content.includes('M88') || content.includes('TSC') ||
                       content.includes('M51') || content.includes('coolantPressure');
        if (!hasTSC) {
            score -= 8;
            optimizations.push({ level: 'warning', text: 'Through-spindle coolant (TSC) not configured' });
        }
        // === TOOL PRELOAD ===
        const hasPreload = content.includes('preloadTool') || content.includes('nextTool') ||
                          content.includes('MT=') || content.includes('T') && content.includes('M6');
        if (!hasPreload) {
            score -= 6;
            optimizations.push({ level: 'info', text: 'Tool preload not enabled - adds time between operations' });
        }
        // === PROBING SUPPORT ===
        const hasProbing = content.includes('probing') || content.includes('G65 P') ||
                          content.includes('CYCLE977') || content.includes('G54.4');
        if (!hasProbing) {
            score -= 5;
            optimizations.push({ level: 'info', text: 'Probing/inspection cycles not configured' });
        }
        // === ARC FEED CORRECTION ===
        const hasArcCorrection = content.includes('arcFeed') || content.includes('arcCorrection') ||
                                 content.includes('circularFeed');
        if (!hasArcCorrection) {
            score -= 5;
            optimizations.push({ level: 'info', text: 'No arc feed correction - may cut too fast in tight arcs' });
        }
        // === SSV (SPINDLE SPEED VARIATION) ===
        const hasSSV = content.includes('SSV') || content.includes('M695') ||
                       content.includes('spindle speed variation');
        if (!hasSSV && (machineMode === 'lathe' || content.includes('turning'))) {
            score -= 5;
            optimizations.push({ level: 'info', text: 'SSV (chatter control) not enabled for turning' });
        }
        // === SAFE START BLOCKS ===
        const hasSafeStart = content.includes('safeStart') || content.includes('G28') ||
                            content.includes('G53') || content.includes('G90 G17 G40');
        if (!hasSafeStart) {
            score -= 4;
            optimizations.push({ level: 'info', text: 'Safe start blocks could be improved' });
        }
        // === CYCLE TIME OPTIMIZATION (Okuma-specific) ===
        if (content.includes('OKUMA') || content.includes('okuma') || content.includes('OSP')) {
            const hasOkumaOpt = content.includes('M63') || content.includes('M61') ||
                               content.includes('M64') || content.includes('M216');
            if (!hasOkumaOpt) {
                score -= 8;
                optimizations.push({ level: 'warning', text: 'Okuma cycle time M-codes not enabled (M63/M61/M64)' });
            }
        }
        // Ensure minimum score
        score = Math.max(score, 20);

        // Sort optimizations by severity
        const levels = { error: 0, warning: 1, info: 2 };
        optimizations.sort((a, b) => levels[a.level] - levels[b.level]);

        // Take top 3
        const topOpts = optimizations.slice(0, 3);
        while (topOpts.length < 3) {
            topOpts.push({ level: 'info', text: 'Custom machine-specific optimizations available' });
        }
        // Update UI
        document.getElementById('postDetectedFormat')?.textContent = format;
        document.getElementById('postLineCount')?.textContent = lineCount.toLocaleString() + ' lines';
        document.getElementById('postCamSoftware')?.textContent = camSoftware;

        const scoreEl = document.getElementById('postOptScore');
        scoreEl.textContent = score + '/100';
        if (score >= 80) {
            scoreEl.style.color = 'var(--success)';
        } else if (score >= 60) {
            scoreEl.style.color = 'var(--warning)';
        } else {
            scoreEl.style.color = '#ef4444';
        }
        // Update optimizations display
        document.getElementById('postOpt1')?.textContent = topOpts[0]?.text || '--';
        document.getElementById('postOpt2')?.textContent = topOpts[1]?.text || '--';
        document.getElementById('postOpt3')?.textContent = topOpts[2]?.text || '--';

        // Calculate estimated improvements based on missing features
        const missingFeatures = optimizations.length;
        const timeImprovement = Math.min(35, 5 + missingFeatures * 3);  // Up to 35% cycle time reduction
        const codeImprovement = Math.min(50, 10 + missingFeatures * 4); // Up to 50% cleaner code
        const featureCount = Math.min(15, optimizations.length + 3);    // New features count

        document.getElementById('postSavingTime')?.textContent = timeImprovement + '%';
        document.getElementById('postSavingCode')?.textContent = codeImprovement + '%';
        document.getElementById('postSavingFeatures')?.textContent = '+' + featureCount;

        // Store analysis for purchase
        uploadedPostProcessor.analysis = {
            score: score,
            optimizations: optimizations,
            format: format,
            camSoftware: camSoftware
        };
        postAnalysisComplete = true;

        // Run comparison against optimized post
        runPostComparison();

        // Show comparison popup modal
        showPostComparisonPopup(score, optimizations, format, camSoftware, timeImprovement, codeImprovement, featureCount);
    }
