    // ALARM CODE TROUBLESHOOTER FUNCTIONS

    /**
     * Quick alarm lookup from common alarm buttons
     */
    function quickAlarmLookup(code, controller) {
        document.getElementById('alarmCodeInput')?.value = code;
        document.getElementById('alarmControllerSelect')?.value = controller;
        lookupAlarmCodeUI();
    }
    /**
     * Main alarm lookup function - called from UI
     */
    function lookupAlarmCodeUI() {
        const alarmCode = document.getElementById('alarmCodeInput')?.value.trim();
        let controllerId = document.getElementById('alarmControllerSelect')?.value;

        if (!alarmCode) {
            alert('Please enter an alarm code');
            return;
        }
        // Auto-detect controller from selected machine
        if (controllerId === 'auto') {
            const machineId = document.getElementById('machineSelect')?.value;
            controllerId = detectControllerFromMachine(machineId);
        }
        // Look up the alarm
        const result = lookupAlarmCode(controllerId, alarmCode);

        // Display results
        displayAlarmResults(result, alarmCode, controllerId);
    }
    /**
     * Detect controller type from machine selection
     */
    function detectControllerFromMachine(machineId) {
        if (!machineId) return 'haas_ngc'; // Default

        const id = machineId.toLowerCase();

        if (id.includes('haas')) return 'haas_ngc';
        if (id.includes('okuma') && (id.includes('lathe') || id.includes('lb') || id.includes('genos_l'))) return 'okuma_osp_p300l';
        if (id.includes('okuma')) return 'okuma_osp_p300m';
        if (id.includes('hurco')) return 'hurco_winmax';
        if (id.includes('mazak')) return 'mazak_smooth';
        if (id.includes('dmg') || id.includes('mori')) return 'dmgmori_celos';
        if (id.includes('makino')) return 'makino_pro';
        if (id.includes('hermle') || id.includes('heidenhain')) return 'heidenhain_tnc640';
        if (id.includes('brother')) return 'brother_cnc';
        if (id.includes('doosan') || id.includes('fanuc')) return 'fanuc_0i';
        if (id.includes('siemens')) return 'siemens_840d';

        return 'fanuc_0i'; // Generic fallback
    }
