    /**
     * Show manual fix instructions
     */
    function showFixInstructions(alarmCode) {
        const controllerId = document.getElementById('alarmControllerSelect')?.value;
        const result = lookupAlarmCode(controllerId === 'auto' ? 'haas_ngc' : controllerId, alarmCode);

        if (!result.found || !result.alarm.postFixes) {
            alert('No manual fix instructions available for this alarm.');
            return;
        }
        const fixes = result.alarm.postFixes;
        let instructions = `Manual Fix Instructions for Alarm ${alarmCode}\n\n`;
        instructions += `In your post processor, locate and disable/modify the following:\n\n`;

        fixes.forEach((fix, i) => {
            instructions += `${i + 1}. Find code: ${fix.code}\n`;
            instructions += `   Action: ${fix.description}\n`;
            instructions += `   Property: ${fix.option}\n\n`;
        });

        instructions += `\nNote: These changes require editing your .cps or post processor file.\n`;
        instructions += `For Fusion 360, the property is usually in the "Post Properties" dialog.`;

        alert(instructions);
    }
