        if (scoreEl) {
            scoreEl.textContent = comparison.uploadedScore + '/100';
            if (comparison.uploadedScore >= 80) {
                scoreEl.style.color = 'var(--success)';
            } else if (comparison.uploadedScore >= 60) {
                scoreEl.style.color = 'var(--warning)';
            } else {
                scoreEl.style.color = '#ef4444';
            }
        }
        // Update improvement estimates based on comparison
        const cycleSaving = Math.min(35, Math.round(comparison.potentialImprovement * 0.35));
        const codeSaving = Math.min(50, Math.round(comparison.potentialImprovement * 0.5));
        const featureCount = comparison.improvements.length;

        document.getElementById('postSavingTime')?.textContent = cycleSaving + '%';
        document.getElementById('postSavingCode')?.textContent = codeSaving + '%';
        document.getElementById('postSavingFeatures')?.textContent = '+' + featureCount;

        // Show top 3 missing features
        const top3 = comparison.improvements.slice(0, 3);
        document.getElementById('postOpt1')?.textContent = top3[0]?.feature || 'All features present';
        document.getElementById('postOpt2')?.textContent = top3[1]?.feature || '--';
        document.getElementById('postOpt3')?.textContent = top3[2]?.feature || '--';

        // Store for later use
        uploadedPostProcessor.comparisonResults = comparison;

