        code += `<span style="color: var(--warning);">; SAFE START BLOCK</span>\n`;
        code += `<span style="color: var(--primary);">G40 G49 G80 G17 G90</span>\n`;
        code += `<span style="color: var(--primary);">G21</span> <span style="color: var(--text-muted);">; Metric</span>\n`;
        code += `<span style="color: var(--primary);">G28 G91 Z0</span> <span style="color: var(--text-muted);">; Safe Z</span>\n\n`;

