            code += `<span style="color: var(--primary);">${features.hsm.split(' ')[0]}</span> <span style="color: var(--text-muted);">; ${features.hsm.includes('(') ? features.hsm.split('(')[1].replace(')', '') : 'High-speed mode'}</span>\n\n`;
