        code += `<span style="color: var(--primary);">T1 M6</span> <span style="color: var(--text-muted);">; 0.500 ENDMILL</span>\n`;
        code += `<span style="color: var(--primary);">G43 H1 Z50.</span>\n`;
        code += `<span style="color: var(--primary);">S12000 M3</span>\n`;
        code += `<span style="color: var(--primary);">M8</span> <span style="color: var(--text-muted);">; Coolant on</span>\n\n`;

