            'HSM': '#eab308',
            '5-Axis': '#a855f7',
            'Macros': '#6366f1',
            'Transform': '#64748b',
            'Data': '#84cc16',
            'Special': '#78716c',
            'Cycles': '#ec4899',
            'Probing': '#06b6d4',
            'Program Control': '#ef4444',
            'Spindle': '#22c55e',
            'Tool Change': '#f59e0b',
            'Coolant': '#3b82f6',
            'Clamping': '#8b5cf6',
            'Auxiliary': '#64748b',
            'Rotary': '#a855f7',
            'Subprograms': '#6366f1',
            'User': '#78716c',
            'Door': '#14b8a6',
            'Pallet': '#f97316',
            'Tapping': '#ec4899',
            'Control': '#84cc16',
            'Turret': '#eab308',
